var Helpers = {
    mainUrl: 'http://localhost/groupbrowsing/portal/v0.3/', // http://hexsee.dev2.gkxim.com/portal/ http://portal.hexsee.com
    //mainUrl: 'http://192.168.1.59/groupbrowsing/portal/v0.3/',
    avatarDefault: 'data:image/png;base64,',
    screen: 1080
}
$(document).ready(function () {
    $('.image-load').addClass('animated fadeInUp');
    isDeviceBody();
	
    $(window).resize(function () {
        isDeviceBody();
    })
});

function isDeviceBody() {
    if ($(window).width() <= Helpers.screen) {
        $('body').addClass('IsDevice');
    } else {
        $('body').removeClass('IsDevice');
    }
}


